var ZeroAddress = common.HexToAddress("0x0000000000000000000000000000000000000000")

var gnosisForwarderAbi abi.ABI

func init() {
	var err error
	gnosisForwarderAbi, err = abi.JSON(strings.NewReader(GnosisForwarderAbi))
	if err != nil {
		panic(err)
	}
}

func NewGnosisForwarderClient(
	client *ethclient.Client,
	forwarderAddress common.Address,
	fromAddress common.Address,
	chainId *big.Int,
) *GnosisForwarderClient {
	return &GnosisForwarderClient{
		ForwarderClientBase{
			client:           client,
			forwarderAddress: forwarderAddress,
			fromAddress:      fromAddress,
			forwarderAbi:     gnosisForwarderAbi,
			chainId:          chainId,
		},
	}
}

func (c *GnosisForwarderClient) GetOnChainNonce() (*big.Int, error) {
	packed, err := c.forwarderAbi.Pack("nonce")
	if err != nil {
		return nil, err
	}

	data := eth.CallMsg{
		To:    &c.forwarderAddress,
		Data:  packed,
		From:  c.fromAddress,
		Gas:   200000,
		Value: big.NewInt(0),
	}

	callOpts := &bind.CallOpts{From: c.fromAddress, Context: context.Background()}
	res, err := c.client.CallContract(callOpts.Context, data, nil)
	if err != nil {
		return nil, err
	}

	p, err := c.forwarderAbi.Unpack("nonce", res)
	if err != nil {
		return nil, err
	}

	nonce := p[0].(*big.Int)

	return nonce, nil
}

// Locks nonce access and returns the next nonce
func (c *GnosisForwarderClient) LockAndNextNonce() (*big.Int, error) {
	c.forwarderNonceLock.Lock()

	if c.forwarderNonce == nil {
		nonce, err := c.GetOnChainNonce()
		if err != nil {
			c.forwarderNonceLock.Unlock()
			return nil, err
		}
		return nonce, nil
	} else {
		return c.forwarderNonce.Add(c.forwarderNonce, big.NewInt(1)), nil
	}
}

// Generate the 712 type hash for signing
func (c *GnosisForwarderClient) TypedHash(
	from, to string,
	data []byte,
	value, gas *math.HexOrDecimal256,
	nonce *big.Int,
	verifyingContract string) ([]byte, *[32]byte, *[32]byte, error) {

	chainId := math.NewHexOrDecimal256(int64(c.chainId.Uint64()))
	const operation uint8 = 0
	baseGas := math.NewHexOrDecimal256(0)
	gasPrice := math.NewHexOrDecimal256(0)

	typedData := signer.TypedData{
		Types: signer.Types{
			// EIP712Domain(uint256 chainId,address verifyingContract)"
			"EIP712Domain": []signer.Type{
				{Name: "chainId", Type: "uint256"},
				{Name: "verifyingContract", Type: "address"},
			},
			// SafeTx(address to,uint256 value,bytes data,uint8 operation,uint256 safeTxGas,uint256 baseGas,uint256 gasPrice,address gasToken,address refundReceiver,uint256 nonce)
			"SafeTx": []signer.Type{
				{Name: "to", Type: "address"},
				{Name: "value", Type: "uint256"},
				{Name: "data", Type: "bytes"},
				{Name: "operation", Type: "uint8"},
				{Name: "safeTxGas", Type: "uint256"},
				{Name: "baseGas", Type: "uint256"},
				{Name: "gasPrice", Type: "uint256"},
				{Name: "gasToken", Type: "address"},
				{Name: "refundReceiver", Type: "address"},
				{Name: "nonce", Type: "uint256"},
			},
		},
		// domain hash - 0x47e79534a245952e8b16893a336b85a3d9ea9fa8c573f3d803afb92a79469218
		PrimaryType: "SafeTx",
		Domain: signer.TypedDataDomain{
			ChainId:           chainId,
			VerifyingContract: verifyingContract,
		},
		Message: signer.TypedDataMessage{
			"to":             to,
			"value":          value,
			"data":           data,
			"operation":      fmt.Sprintf("%d", operation),
			"safeTxGas":      gas,
			"baseGas":        baseGas,
			"gasPrice":       gasPrice,
			"gasToken":       ZeroAddress.Hex(),
			"refundReceiver": ZeroAddress.Hex(),
			"nonce":          math.NewHexOrDecimal256(nonce.Int64()),
		},
	}

	domainSeparator, err := typedData.HashStruct("EIP712Domain", typedData.Domain.Map())

	if err != nil {
		return nil, nil, nil, err
	}
	var fixedSizeDomainSeperator [32]byte
	copy(fixedSizeDomainSeperator[:], domainSeparator)

	typedDataHash, err := typedData.HashStruct(typedData.PrimaryType, typedData.Message)
	if err != nil {
		return nil, nil, nil, err
	}

	// type hash - 0xbb8310d486368db6bd6f849402fdd73ad53d316b5a4b2644ad6efe0f941286d8
	var fixedSizeTypeHash [32]byte
	copy(fixedSizeTypeHash[:], typedData.TypeHash(typedData.PrimaryType))

	rawData := []byte(fmt.Sprintf("\x19\x01%s%s", string(domainSeparator), string(typedDataHash)))
	return crypto.Keccak256(rawData), &fixedSizeDomainSeperator, &fixedSizeTypeHash, nil
}

// Pack and sign forwarder contract 'execute' arguments
func (c *GnosisForwarderClient) PackAndSignForwarderArg(
	from, to common.Address,
	data []byte,
	nonce, value, gas *big.Int,
	kp secp256k1.Keypair) ([]byte, error) {

	forwarderHash, _, _, err := c.TypedHash(
		from.String(),
		to.String(),
		data,
		math.NewHexOrDecimal256(int64(value.Uint64())),
		math.NewHexOrDecimal256(int64(gas.Uint64())),
		nonce,
		c.forwarderAddress.String(),
	)

	if err != nil {
		return nil, err
	}

	// now sign that hash
	sig, err := crypto.Sign(forwarderHash, kp.PrivateKey())
	sig[64] += 27 // Transform V from 0/1 to 27/28

	if err != nil {
		return nil, err
	}

	packed, err := c.forwarderAbi.Pack("execTransaction",
		to,
		value,
		data,
		uint8(0),
		gas,
		big.NewInt(0),
		big.NewInt(0),
		ZeroAddress,
		ZeroAddress,
		sig)

	if err != nil {
		return nil, err
	}

	return packed, nil
}
